drop database if exists shop_sc;
create database shop_sc character set utf8;
use shop_sc;
drop table if exists shop_sc.admin;
create table shop_sc.admin
(
	username char(16) not null primary key,
	password char(40) not null
);
drop table if exists shop_sc.product;
create table shop_sc.product
(
   productid int(10) unsigned not null auto_increment primary key,
   farsiname char(60) not null,
   englishname char(60) not null,
   description text not null,
   catid int(10) unsigned not null,
   price decimal(10,0) not null,
   dat datetime not null,
   imageurl char(60)not null
);
drop table if exists shop_sc.categories;
create table shop_sc.categories
(
  catid int(10) unsigned not null auto_increment primary key,
  catname char(60) not null
);
drop table if exists shop_sc.order;
create table shop_sc.order
(
   orderid int(10) unsigned not null auto_increment primary key,
   productid int(10) unsigned not null,
   items int(10) not null,
   total_price decimal(10,0) not null,
   name char(50) not null,
   tel char(50) not null,
   address text not null,
   zipcode char(50) not null
);
grant select, insert, update, delete
on shop_sc.*
to shop_sc@localhost identified by 'password';

